import{_ as e,c as r,o as t,a as l}from"./app.4fc0885a.js";const g=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"CDN","slug":"cdn","link":"#cdn","children":[{"level":3,"title":"\u4E24\u4E2A\u6838\u5FC3\uFF1A","slug":"\u4E24\u4E2A\u6838\u5FC3","link":"#\u4E24\u4E2A\u6838\u5FC3","children":[]},{"level":3,"title":"\u56DE\u6E90\u7B56\u7565","slug":"\u56DE\u6E90\u7B56\u7565","link":"#\u56DE\u6E90\u7B56\u7565","children":[]},{"level":3,"title":"\u963F\u91CC\u4E91 CDN \u5F00\u542F\u6B65\u9AA4","slug":"\u963F\u91CC\u4E91-cdn-\u5F00\u542F\u6B65\u9AA4","link":"#\u963F\u91CC\u4E91-cdn-\u5F00\u542F\u6B65\u9AA4","children":[]}]}],"relativePath":"2024/computer-networks/CDN\u76F8\u5173.md"}'),n={name:"2024/computer-networks/CDN\u76F8\u5173.md"},i=l('<h2 id="cdn" tabindex="-1">CDN <a class="header-anchor" href="#cdn" aria-hidden="true">#</a></h2><p><strong>CDN \uFF08Content Delivery Network \uFF09\u5185\u5BB9\u5206\u53D1\u7F51\u7EDC</strong>\u3002</p><p>\u5185\u5BB9\u5206\u53D1\u7F51\u7EDC\uFF08Content Delivery Network\uFF0CCDN\uFF09\u662F\u5EFA\u7ACB\u5E76\u8986\u76D6\u5728\u627F\u8F7D\u7F51\u4E0A\uFF0C\u7531\u4E0D\u540C\u533A\u57DF\u7684\u670D\u52A1\u5668\u7EC4\u6210\u7684\u5206\u5E03\u5F0F\u7F51\u7EDC\u3002\u5C06\u6E90\u7AD9\u8D44\u6E90\u7F13\u5B58\u5230\u5168\u56FD\u5404\u5730\u7684\u8FB9\u7F18\u670D\u52A1\u5668\uFF0C\u4F9B\u7528\u6237\u5C31\u8FD1\u83B7\u53D6\uFF0C\u964D\u4F4E\u6E90\u7AD9\u538B\u529B\u3002</p><h3 id="\u4E24\u4E2A\u6838\u5FC3" tabindex="-1">\u4E24\u4E2A\u6838\u5FC3\uFF1A <a class="header-anchor" href="#\u4E24\u4E2A\u6838\u5FC3" aria-hidden="true">#</a></h3><ul><li><strong>\u7F13\u5B58</strong></li><li><strong>\u56DE\u6E90</strong> \uFF08CDN \u53D1\u73B0\u81EA\u5DF1\u6CA1\u6709\u8FD9\u4E2A\u8D44\u6E90\uFF0C\u5411\u6E90\u7AD9\u8981\u8FD9\u4E2A\u8D44\u6E90\uFF09</li></ul><p>CDN \u7684\u6838\u5FC3\u70B9\u6709\u4E24\u4E2A\uFF0C\u4E00\u4E2A\u662F<strong>\u7F13\u5B58</strong>\uFF0C\u4E00\u4E2A\u662F<strong>\u56DE\u6E90</strong>\u3002</p><p>\u8FD9\u4E24\u4E2A\u6982\u5FF5\u90FD\u975E\u5E38\u597D\u7406\u89E3\u3002\u5BF9\u6807\u5230\u4E0A\u9762\u63CF\u8FF0\u7684\u8FC7\u7A0B\uFF0C\u201C\u7F13\u5B58\u201D\u5C31\u662F\u8BF4\u6211\u4EEC\u628A\u8D44\u6E90 copy \u4E00\u4EFD\u5230 CDN \u670D\u52A1\u5668\u4E0A\u8FD9\u4E2A\u8FC7\u7A0B\uFF0C\u201C\u56DE\u6E90\u201D\u5C31\u662F\u8BF4 CDN \u53D1\u73B0\u81EA\u5DF1\u6CA1\u6709\u8FD9\u4E2A\u8D44\u6E90\uFF08\u4E00\u822C\u662F\u7F13\u5B58\u7684\u6570\u636E\u8FC7\u671F\u4E86\uFF09\uFF0C\u8F6C\u5934\u5411\u6839\u670D\u52A1\u5668\uFF08\u6216\u8005\u5B83\u7684\u4E0A\u5C42\u670D\u52A1\u5668\uFF09\u53BB\u8981\u8FD9\u4E2A\u8D44\u6E90\u7684\u8FC7\u7A0B\u3002</p><h3 id="\u56DE\u6E90\u7B56\u7565" tabindex="-1">\u56DE\u6E90\u7B56\u7565 <a class="header-anchor" href="#\u56DE\u6E90\u7B56\u7565" aria-hidden="true">#</a></h3><p>CDN \u7684\u56DE\u6E90\u7B56\u7565\u6307\u7684\u662F\u5F53 CDN \u8FB9\u7F18\u8282\u70B9\u65E0\u6CD5\u76F4\u63A5\u54CD\u5E94\u7528\u6237\u8BF7\u6C42\u65F6,\uFF08\u672C\u5730\u7F13\u5B58\u672A\u547D\u4E2D\u3001\u7F13\u5B58\u5DF2\u8FC7\u671F\u3001\u6216\u8005\u6839\u636E\u914D\u7F6E\u89C4\u5219\u9700\u8981\u56DE\u6E90\u83B7\u53D6\u6700\u65B0\u8D44\u6E90\u65F6\uFF09\uFF0C\u9700\u8981\u4ECE\u4E0A\u6E38\u83B7\u53D6\u6240\u9700\u8D44\u6E90\u7684\u7B56\u7565\u548C\u89C4\u5219\u3002</p><p>CDN \u7684\u56DE\u6E90\u7B56\u7565\u901A\u5E38\u5305\u62EC\u4EE5\u4E0B\u51E0\u4E2A\u65B9\u9762:</p><ol><li><strong>\u5C31\u8FD1\u539F\u5219</strong> \u5F53\u7528\u6237\u8BF7\u6C42\u67D0\u4E2A\u8D44\u6E90\u65F6,CDN \u8FB9\u7F18\u8282\u70B9\u4F1A\u4F18\u5148\u4ECE\u5C31\u8FD1\u7684\u5176\u4ED6\u8FB9\u7F18\u8282\u70B9\u83B7\u53D6\u8BE5\u8D44\u6E90\u3002\u5982\u679C\u65E0\u6CD5\u83B7\u53D6,\u624D\u4F1A\u5411\u66F4\u8FDC\u7684\u8FB9\u7F18\u8282\u70B9\u6216\u8005\u56DE\u6E90\u7AD9\u70B9\u8BF7\u6C42\u8D44\u6E90\u3002\u8FD9\u6837\u53EF\u4EE5\u6700\u5927\u9650\u5EA6\u51CF\u5C11\u7F51\u7EDC\u4F20\u8F93\u65F6\u5EF6,\u63D0\u9AD8\u54CD\u5E94\u901F\u5EA6\u3002</li><li><strong>\u7F13\u5B58\u4F18\u5148</strong> \u8FB9\u7F18\u8282\u70B9\u4E0A\u4F1A\u7F13\u5B58\u5E38\u7528\u8D44\u6E90,\u5F53\u6536\u5230\u7528\u6237\u8BF7\u6C42\u65F6,\u4F18\u5148\u547D\u4E2D\u672C\u5730\u7F13\u5B58\u63D0\u4F9B\u670D\u52A1\u3002\u53EA\u6709\u5F53\u672C\u5730\u7F13\u5B58\u672A\u547D\u4E2D\u65F6,\u624D\u4F1A\u5C1D\u8BD5\u4ECE\u5176\u4ED6\u8282\u70B9\u83B7\u53D6\u6216\u56DE\u6E90\u3002</li><li><strong>\u9884\u70ED\u66F4\u65B0</strong> \u5BF9\u4E8E\u4E00\u4E9B\u91CD\u8981\u7684\u8D44\u6E90,CDN \u670D\u52A1\u5546\u4F1A\u5728\u5185\u5BB9\u53D1\u5E03\u524D,\u4E3B\u52A8\u5C06\u8D44\u6E90\u5206\u53D1\u9884\u70ED\u5230\u5404\u8FB9\u7F18\u8282\u70B9\u7684\u7F13\u5B58\u4E2D,\u4EE5\u4FBF\u7B2C\u4E00\u4E2A\u7528\u6237\u8BF7\u6C42\u65F6\u80FD\u591F\u76F4\u63A5\u547D\u4E2D\u7F13\u5B58\u3002</li><li><strong>\u5185\u5BB9\u5F52\u7C7B</strong> \u670D\u52A1\u5546\u4F1A\u6839\u636E\u5185\u5BB9\u7684\u91CD\u8981\u6027\u3001\u70ED\u70B9\u7A0B\u5EA6\u7B49\u5BF9\u8D44\u6E90\u8FDB\u884C\u5206\u7C7B,\u5236\u5B9A\u4E0D\u540C\u7684\u7F13\u5B58\u7B56\u7565\u548C\u56DE\u6E90\u7B56\u7565\u3002\u70ED\u95E8\u8D44\u6E90\u53EF\u4EE5\u5728\u66F4\u591A\u8FB9\u7F18\u8282\u70B9\u7F13\u5B58,\u4E0D\u592A\u70ED\u95E8\u7684\u8D44\u6E90\u5219\u7F13\u5B58\u65F6\u95F4\u8F83\u77ED\u3002</li><li><strong>\u8D1F\u8F7D\u5747\u8861</strong> CDN \u4E00\u822C\u6709\u591A\u4E2A\u56DE\u6E90\u7AD9\u70B9,\u6839\u636E\u8D1F\u8F7D\u60C5\u51B5\u8FDB\u884C\u667A\u80FD\u8C03\u5EA6,\u9632\u6B62\u5355\u4E2A\u56DE\u6E90\u7AD9\u70B9\u8FC7\u8F7D\u3002</li><li><strong>\u5BB9\u707E\u5907\u4EFD</strong> \u5F53\u4E3B\u56DE\u6E90\u7AD9\u70B9\u51FA\u73B0\u6545\u969C\u65F6,\u53EF\u4EE5\u5207\u6362\u81F3\u5907\u4EFD\u56DE\u6E90\u7AD9\u70B9\u83B7\u53D6\u8D44\u6E90,\u4FDD\u8BC1\u4E1A\u52A1\u8FDE\u7EED\u6027\u3002</li></ol><p>\u603B\u7684\u6765\u8BF4,CDN \u7684\u56DE\u6E90\u7B56\u7565\u65E8\u5728\u5C3D\u91CF\u8BA9\u7528\u6237\u5C31\u8FD1\u83B7\u53D6\u8D44\u6E90\u3001\u63D0\u9AD8\u54CD\u5E94\u901F\u5EA6\u3001\u8282\u7701\u5E26\u5BBD\u6210\u672C,\u5E76\u4FDD\u8BC1\u8D44\u6E90\u7684\u9AD8\u53EF\u7528\u6027\u3002\u4E0D\u540C\u670D\u52A1\u5546\u4F1A\u6839\u636E\u81EA\u8EAB\u7F51\u7EDC\u6761\u4EF6\u3001\u4E1A\u52A1\u9700\u6C42\u7B49\u5236\u5B9A\u5177\u4F53\u7684\u56DE\u6E90\u7B56\u7565\u3002</p><p><strong>PS\uFF1A\u963F\u91CC\u4E91\u56DE\u6E90\u7B56\u7565\uFF1A</strong></p><ol><li>\u7528\u6237 100%\u56DE\u6E90\u6D41\u91CF\u90FD\u5C06\u9996\u5148\u56DE\u6E90\u4F18\u5148\u7EA7\u4E3A\u201C\u4E3B\u201D\u7684\u6E90\u7AD9\uFF0C\u5982\u679C\u67D0\u4E2A\u6E90\u7AD9\u5065\u5EB7\u68C0\u67E5\u8FDE\u7EED 3 \u6B21\u90FD\u662F\u5931\u8D25\u7684\u8BDD\uFF0C\u5219 100%\u7684\u6D41\u91CF\u90FD\u5C06\u9009\u62E9\u4F18\u5148\u7EA7\u4E3A\u201C\u6B21\u201D\u7684\u6E90\u7AD9\u56DE\u6E90\u3002</li><li>\u5982\u679C\u4E3B\u52A8\u5065\u5EB7\u68C0\u67E5\u6210\u529F\u7684\u8BDD\uFF0C\u8BE5\u6E90\u7AD9\u5C31\u4F1A\u91CD\u65B0\u6807\u8BB0\u4E3A\u53EF\u7528\uFF0C\u6062\u590D\u539F\u6765\u4F18\u5148\u7EA7\u3002\u5F53\u6240\u6709\u6E90\u7AD9\u7684\u56DE\u6E90\u4F18\u5148\u7EA7\u4E00\u6837\u65F6\uFF0CCDN \u5C06\u81EA\u52A8\u8F6E\u8BE2\u56DE\u6E90</li></ol><p><a href="https://help.aliyun.com/knowledge_detail/40121.html?spm=a2c6h.13066369.0.0.100e5084r2erfr" target="_blank" rel="noreferrer">CDN \u63A7\u5236\u53F0\u53EF\u4EE5\u914D\u7F6E\u591A\u4E2A\u6E90\u7AD9\uFF1A</a></p><p>\u5728\u8BBE\u7F6E\u5B8C\u6210\u540E\uFF0CCDN \u56DE\u6E90\u7684\u65F6\u5019\u5C31\u4F1A\u6309\u7167\u8BBE\u7F6E\u7684\u591A\u4E2A\u6E90\u7AD9\u91C7\u7528\u8F6E\u8BE2\u7684\u65B9\u5F0F\u8FDB\u884C\u56DE\u6E90\u8BF7\u6C42\u3002\u56E0\u6B64\uFF0C\u9700\u8981\u7528\u6237\u4FDD\u8BC1\u5404\u6E90\u7AD9\u5BF9\u4E8E\u7684\u7AD9\u70B9\u5185\u5BB9\u8981\u4E00\u81F4\uFF0C\u5426\u5219\u4F1A\u5BFC\u81F4 CDN \u83B7\u53D6\u5230\u7684\u6570\u636E\u5B58\u5728\u5DEE\u5F02\u3002</p><h3 id="\u963F\u91CC\u4E91-cdn-\u5F00\u542F\u6B65\u9AA4" tabindex="-1">\u963F\u91CC\u4E91 CDN \u5F00\u542F\u6B65\u9AA4 <a class="header-anchor" href="#\u963F\u91CC\u4E91-cdn-\u5F00\u542F\u6B65\u9AA4" aria-hidden="true">#</a></h3><ol><li>\u6DFB\u52A0\u9700\u8981\u52A0\u901F\u57DF\u540D\uFF0C\u914D\u7F6E\u6E90\u7AD9\u4FE1\u606F \uFF08OSS\u3001IP \u7B49\uFF09</li><li>\u63A8\u8350\u914D\u7F6E \uFF08\u7F13\u5B58\u8FC7\u671F\u65F6\u95F4\u3001\u5E26\u5BBD\u5C01\u9876\u3001\u76D1\u63A7\u8B66\u544A\u3001\u9875\u9762\u4F18\u5316\u3001\u667A\u80FD\u538B\u7F29\u7B49\uFF09</li><li>\u5B8C\u6210 CNAME \u914D\u7F6E</li></ol><hr><ol><li>A \u8BB0\u5F55\u5C31\u662F\u628A\u4E00\u4E2A\u57DF\u540D\u89E3\u6790\u5230\u4E00\u4E2A IP \u5730\u5740\uFF08Address\uFF0C\u7279\u5236\u6570\u5B57 IP \u5730\u5740\uFF09</li><li>CNAME \u8BB0\u5F55\u5C31\u662F\u628A\u57DF\u540D\u89E3\u6790\u5230\u53E6\u5916\u4E00\u4E2A\u57DF\u540D</li></ol><p>\u4E00\u4E2A CDN \u7F51\u7EDC\u5F80\u5F80\u6709\u975E\u5E38\u591A\u7684\u8FB9\u9645(edge)\u8282\u70B9\uFF0C\u5F53\u4F60\u8D2D\u4E70\u4E86 CDN \u670D\u52A1\u53C8\u60F3\u7528\u81EA\u5DF1\u7684\u57DF\u540D\u7684\u65F6\u5019\uFF0C\u76F4\u63A5\u628A\u4F60\u7684\u57DF\u540D CNAME \u5230 CDN \u7684\u57DF\u540D\u5C31\u597D\u4E86\uFF0C\u7136\u540E\u5F53\u7528\u6237\u8FDE\u63A5\u7684\u65F6\u5019\u5F80\u5F80\u80FD\u591F\u8FDE\u63A5\u5230\u4ED6\u4EEC\u6700\u8FD1\u7684\u8282\u70B9\u3002</p>',21),o=[i];function s(a,d,c,h,p,N){return t(),r("div",null,o)}const D=e(n,[["render",s]]);export{g as __pageData,D as default};
